/*** This program is used to generate datasets for SubsetSumGA code.*  * Author : Jahanzeb Maqbool* Ajou University, Dept. of Computer Engineering, South Korea.*/#include <stdio.h>#include <cstdlib>#include <stdlib.h>#include <time.h>#include <string.h>#include <fstream>//static const int SET_SIZE = 100000;void saveValues (char* filename, int numOfVals, int* values){		FILE* file;	int rv;	int idx;		file = fopen(filename, "w+");	if(file == NULL) {	      printf("ERROR: Unable to open file `%s'.\n", filename);	      exit(1);	}		rv = fprintf(file, "%d\n", numOfVals);	for (idx = 0 ; idx < numOfVals ; idx ++)			rv = fprintf(file, "%d\n", values[idx]);		if(rv < 0) {           printf("ERROR: Unable to write to file `%s'.\n", filename);           fclose(file);           exit(1);        }	fclose (file);       	}void print(int* arr, int numOfVals) {	printf ("{");	for (int i = 0; i < numOfVals; i++) {		printf ("%d", arr[i]);		if (i != numOfVals-1) printf (", ");	}	printf ("}\n");}main(int argc, char **argv){	char* outputFile = argv [1];		char* randLimit  = argv [2];	char* dataLen    = argv [3];	int * values;	time_t seconds;		int numOfVals = atoi (dataLen);	values = (int*) malloc(numOfVals*sizeof(int));		time(&seconds);	srand((unsigned int) seconds);		for (int i = 0; i < numOfVals; i++) {		int r = rand() % atoi(randLimit);		//printf ("Random Number %d\n", r);			values [i] = r;	}	//print (values, numOfVals);		saveValues (outputFile, numOfVals, values);		printf("\n");	return 0;}                                      